// グローバルコンテキストに設定を保存
let config = global.get('sensorConfig') || {};

switch(msg.topic) {
    case 'sensorId':
        config.sensorId = msg.payload;
        break;
    case 'location':
        config.latitude = msg.payload.latitude;
        config.longitude = msg.payload.longitude;
        config.description = msg.payload.description;
        break;
    case 'parameters':
        config.baseTemp = msg.payload.baseTemp;
        config.variation = msg.payload.variation;
        config.interval = msg.payload.interval;
        break;
    case 'anomaly':
        config.anomalyType = msg.payload.anomalyType;
        config.anomalyProbability = msg.payload.probability;
        config.anomalyIntensity = msg.payload.intensity;
        break;
}

global.set('sensorConfig', config);
return msg;

initialize:
// 初期設定
let config = {
    sensorId: 'urn:ngsi-ld:TemperatureSensor:001',
    latitude: 35.6812,
    longitude: 139.7671,
    description: 'Temperature Sensor 001',
    baseTemp: 20,
    variation: 0.5,
    interval: 1,
    anomalyType: 'none',
    anomalyProbability: 10,
    anomalyIntensity: 3
};
global.set('sensorConfig', config);

func:
// グローバル変数の初期化
if (!context.get('startTime')) {
    context.set('startTime', Date.now());
    context.set('lastTemp', null);
    context.set('anomalyStartTime', null);
    context.set('baselineTemp', null);
}

// 設定を取得
const config = global.get('sensorConfig');
const now = Date.now();

// 基本温度の生成
let temp = config.baseTemp + (Math.random() * 2 - 1) * config.variation;
let status = '正常';

// 異常値の生成
if (config.anomalyType !== 'none') {
    let probability = config.anomalyProbability / 100;

    // 周期的な異常の場合、時間に基づいて確率を調整
    if (config.anomalyType === 'periodic') {
        const elapsed = (now % (config.period * 1000)) / 1000;
        if (elapsed < config.duration) {
            probability = 1;
        } else {
            probability = 0;
        }
    }

    if (Math.random() < probability) {
        switch (config.anomalyType) {
            case 'spike':
                // スパイク異常: 基準値から大きく外れた値
                const direction = Math.random() < 0.5 ? 1 : -1;
                temp += direction * (5 + config.anomalyIntensity * 2);
                status = 'スパイク異常';
                break;

            case 'failure':
                // センサー故障: ノイズの多い値または一定値
                if (Math.random() < 0.5) {
                    // 一定値
                    temp = config.baseTemp;
                    status = 'センサー故障（固定値）';
                } else {
                    // 大きなノイズ
                    temp = config.baseTemp + (Math.random() * 20 - 10) * config.anomalyIntensity;
                    status = 'センサー故障（ノイズ）';
                }
                break;

            case 'communication':
                // 通信エラー: nullまたは不正な値
                if (Math.random() < 0.5) {
                    temp = null;
                    status = '通信エラー（データなし）';
                } else {
                    temp = NaN;
                    status = '通信エラー（不正値）';
                }
                break;

            case 'trend':
                // トレンド変化: 徐々に上昇または下降
                if (!context.get('anomalyStartTime')) {
                    context.set('anomalyStartTime', now);
                    context.set('baselineTemp', temp);
                }

                const minutesElapsed = (now - context.get('anomalyStartTime')) / (60 * 1000);
                const change = config.trendRate * minutesElapsed;
                temp = context.get('baselineTemp') + (config.trendDirection === 'up' ? change : -change);
                status = 'トレンド変化（' + (config.trendDirection === 'up' ? '上昇' : '下降') + '）';
                break;

            case 'periodic':
                // 周期的な異常: 一定間隔で発生
                const anomalyMagnitude = 5 + config.anomalyIntensity * 2;
                temp += anomalyMagnitude;
                status = '周期的な異常';
                break;

            case 'noise':
                // ノイズ増加: 標準偏差の増加
                const noiseLevel = config.variation * (1 + config.anomalyIntensity);
                temp = config.baseTemp + (Math.random() * 2 - 1) * noiseLevel;
                status = 'ノイズ増加';
                break;
        }
    } else if (config.anomalyType === 'trend' && context.get('anomalyStartTime')) {
        // トレンド変化の継続
        const minutesElapsed = (now - context.get('anomalyStartTime')) / (60 * 1000);
        const change = config.trendRate * minutesElapsed;
        temp = context.get('baselineTemp') + (config.trendDirection === 'up' ? change : -change);
        status = 'トレンド変化（' + (config.trendDirection === 'up' ? '上昇' : '下降') + '）';
    }
}

// 前回の値を保存
context.set('lastTemp', temp);

// ゲージとグラフ用のメッセージ
const sensorMsg = {
    payload: {
        temperature: temp,
        timestamp: now
    }
};

// ステータス表示用のメッセージ
const statusMsg = { payload: status };

// Smart Data Models用のメッセージ
const ngsiMsg = {
    payload: {
        "id": config.sensorId,
        "type": "TemperatureSensor",
        "dateObserved": new Date(now).toISOString(),
        "temperature": temp,
        "unit": "CEL",
        "accuracy": 0.1,
        "description": config.description,
        "location": {
            "type": "Point",
            "coordinates": [config.longitude, config.latitude]
        },
        "status": status
    }
};

node.send([sensorMsg, statusMsg, ngsiMsg]);

return [sensorMsg, statusMsg, ngsiMsg];
